import React, { useState } from 'react';
import QuoteForm from './components/QuoteForm';
import ScriptGenerator from './components/ScriptGenerator';
import TrendingTopics from './components/TrendingTopics';
import './assets/styles/main.css';

const App = () => {
    const [script, setScript] = useState('');

    const handleQuoteSubmit = (quote) => {
        const enhancedScript = `讓我們深入探討這句話的深層含義：
『${quote}』

這段話提醒我們，在人生的旅程中，每個選擇都很重要。它不僅反映了我們的價值觀，更展現了我們追求夢想的決心。

這樣的智慧提醒我們：
1. 保持積極正向的心態
2. 相信自己的潛力
3. 持續不斷地努力
4. 勇於面對挑戰

這不只是一句簡單的激勵語錄，而是一份來自內心的覺醒與改變的契機。`;

        setScript(enhancedScript);
    };

    return (
        <div className="app-container">
            <header className="app-header">
                <h1>心靈雞湯短影音腳本生成器</h1>
            </header>
            <main className="app-main">
                <TrendingTopics />
                <QuoteForm onSubmit={handleQuoteSubmit} />
                <ScriptGenerator script={script} />
            </main>
        </div>
    );
};

export default App;
