import React from 'react';

const ScriptGenerator = ({ script }) => {
    if (!script) {
        return (
            <div className="script-output empty">
                <p>在上方輸入文字並點擊「生成腳本」來產生短影音腳本</p>
            </div>
        );
    }

    // 將輸入的文字轉換為腳本格式
    const formatScript = (rawScript) => {
        return `[開場白]
"大家好，今天要跟大家分享一個重要的人生啟示..."

[主要內容]
${rawScript}

[呼籲行動]
"如果這段話觸動了你，請別忘了按讚訂閱，也歡迎在下方留言分享你的想法！"

[背景音樂建議]
柔和的鋼琴音樂，營造溫暖感性的氛圍

[視覺建議]
- 使用溫暖的色調
- 文字動態呈現
- 配上相關意境畫面
- 加入簡單的動態效果`;
    };

    return (
        <div className="script-output">
            <h2>生成的腳本：</h2>
            <pre>{formatScript(script)}</pre>
        </div>
    );
};

export default ScriptGenerator;
