      ÂãµÂøó: {
        stories: ["Êúâ‰∏Ä‰ΩçÈÅãÂãïÂì°ÔºåÂú®ÊúÄÂõ∞Èõ£ÁöÑÊôÇÂàªÔºå{‰∏ªÈ°å}ÊàêÁÇ∫‰∫Ü‰ªñÁπºÁ∫åÂâçÈÄ≤ÁöÑÂãïÂäõ...", "‰∏ÄÂÄãÂâµÊ•≠ËÄÖÁöÑÊïÖ‰∫ãÂëäË®¥ÊàëÂÄëÔºå{‰∏ªÈ°å}ÊòØÊàêÂäüË∑Ø‰∏äÊúÄÈáçË¶ÅÁöÑÂìÅË≥™...", "Áï∂ÊâÄÊúâ‰∫∫ÈÉΩË™™‰∏çÂèØËÉΩÊôÇÔºåÂ•πÁî®{‰∏ªÈ°å}Ë≠âÊòé‰∫ÜÂ•áËπüÁöÑÂ≠òÂú®..."]
      },
      ‰∫∫ÁîüÊÑüÊÇü: {
        stories: ["ÂùêÂú®ÂíñÂï°Âª≥Ë£°ÔºåÁúãËëóÁ™óÂ§ñÁöÑË°å‰∫∫ÔºåÊàëÁ™ÅÁÑ∂Â∞ç{‰∏ªÈ°å}Êúâ‰∫ÜÊñ∞ÁöÑÁêÜËß£...", "ËÆÄÂà∞‰∏ÄÊú¨Êõ∏‰∏≠ÈóúÊñº{‰∏ªÈ°å}ÁöÑÊèèËø∞ÔºåÊàëÈô∑ÂÖ•‰∫ÜÊ∑±Ê∑±ÁöÑÊÄùËÄÉ...", "Âú®‰∫∫ÁîüÁöÑÊüêÂÄãËΩâÊäòÈªû‰∏äÔºå{‰∏ªÈ°å}ËÆìÊàëÈáçÊñ∞ÂØ©Ë¶ñ‰∫ÜËá™Â∑±ÁöÑÂÉπÂÄºËßÄ..."]
      },
      ÊÑõËàáÊ∫´Êöñ: {
        stories: ["‰∏ÄÂÄãÊØçË¶™ÂíåÂ≠©Â≠êÁöÑÊïÖ‰∫ãÔºåËÆìÊàëÊòéÁôΩ‰∫Ü{‰∏ªÈ°å}ÊúÄÁ¥îÁ≤πÁöÑÊ®£Â≠ê...", "Âú®ÈÜ´Èô¢Ë£°ÔºåÊàëÁúãÂà∞‰∫Ü{‰∏ªÈ°å}ÊúÄÁúüÂØ¶„ÄÅÊúÄÊÑü‰∫∫ÁöÑË°®Áèæ...", "‰∏ÄÂ∞çËÄÅÂ§´Â¶ªÁöÑÊó•Â∏∏ÁîüÊ¥ªÔºåË©ÆÈáã‰∫Ü{‰∏ªÈ°å}ÊúÄÊ∑±ÂàªÁöÑÂê´Áæ©..."]
      }
    };
    return elements[category] || elements.ÊàêÈï∑;
  }

  // ÁîüÊàêÊõø‰ª£Ê®ôÈ°å
  generateAlternativeTitles(topic, category) {
    const alternatives = [`${topic}ÔºöÊØèÂÄã‰∫∫ÂøÉ‰∏≠ÁöÑÈÇ£ÈÅìÂÖâ`, `ÈóúÊñº${topic}ÔºåÊàëÂÄëÈÉΩÈúÄË¶ÅÁü•ÈÅìÁöÑ‰∫ã`, `Âú®${topic}‰∏≠Â∞ãÊâæ‰∫∫ÁîüÁöÑÁ≠îÊ°à`, `${topic}ËÉåÂæåÁöÑÊ∑±Â±§ÊÑèÁæ©`];
    return alternatives.slice(0, 3);
  }

  // Áç≤ÂèñÂØ´‰ΩúÊäÄÂ∑ß
  getWritingTips(category) {
    const tips = {
      ÊàêÈï∑: ['Â§ö‰ΩøÁî®ÂÖ∑È´îÁöÑ‰æãÂ≠êÂíåÊïÖ‰∫ã‰æÜË™™ÊòéËßÄÈªû', 'ÂæûËÆÄËÄÖÁöÑËßíÂ∫¶Âá∫ÁôºÔºåËÆìÂÖßÂÆπÊõ¥ÊúâÂÖ±È≥¥', 'ÁµêÂêàÂÄã‰∫∫Á∂ìÊ≠∑ÔºåËÆìÊñáÁ´†Êõ¥ÊúâÁúüÂØ¶ÊÑü'],
      ÂãµÂøó: ['‰ΩøÁî®Á©çÊ•µÊ≠£Èù¢ÁöÑË©ûÂΩôÂíåË™ûË™ø', 'Ë®≠ÁΩÆÊòéÁ¢∫ÁöÑË°åÂãïÂëºÁ±≤ÔºåÊøÄÂãµËÆÄËÄÖÊé°ÂèñË°åÂãï', 'Âä†ÂÖ•Êï∏ÊìöÊàñÊàêÂäüÊ°à‰æãÂ¢ûÂº∑Ë™™ÊúçÂäõ'],
      ‰∫∫ÁîüÊÑüÊÇü: ['‰øùÊåÅÊÄùËÄÉÁöÑÊ∑±Â∫¶ÔºåÈÅøÂÖçÊµÆÊñºË°®Èù¢', 'ÈÅãÁî®ÊØîÂñªÂíåÈ°ûÊØîËÆìÊäΩË±°Ê¶ÇÂøµÊõ¥ÊòìÁêÜËß£', 'Áµ¶ËÆÄËÄÖÁïô‰∏ãÊÄùËÄÉÁöÑÁ©∫Èñì'],
      ÊÑõËàáÊ∫´Êöñ: ['Ê≥®ÈáçÊÉÖÊÑüÁöÑË°®ÈÅîÔºåËÆìÊñáÂ≠óÊúâÊ∫´Â∫¶', '‰ΩøÁî®Ê∫´È¶®ÁöÑÊïÖ‰∫ãÂíåÂ†¥ÊôØ', 'ÁáüÈÄ†Ë¶™ÂàáÂèãÂñÑÁöÑÂØ´‰ΩúÈ¢®Ê†º']
    };
    return tips[category] || tips.ÊàêÈï∑;
  }

  // Áç≤ÂèñÁõ∏Èóú‰∏ªÈ°å
  getRelatedTopics(category) {
    const related = {
      ÊàêÈï∑: ['Ëá™ÊàëÊèêÂçá', 'Â≠∏ÁøíÊàêÈï∑', 'ÂÄã‰∫∫ÁôºÂ±ï'],
      ÂãµÂøó: ['Ê≠£ËÉΩÈáè', 'Â§¢ÊÉ≥ËøΩÊ±Ç', 'Â†ÖÊåÅ‰∏çÊáà'],
      ‰∫∫ÁîüÊÑüÊÇü: ['‰∫∫ÁîüÂì≤ÁêÜ', 'ÁîüÊ¥ªÊô∫ÊÖß', 'ÂÖßÂøÉÂπ≥Èùú'],
      ÊÑõËàáÊ∫´Êöñ: ['ÊÑüÊÅ©', 'ÂèãÊÉÖ', 'ÂÆ∂Â∫≠Ê∫´Êöñ']
    };
    return related[category] || related.ÊàêÈï∑;
  }

  // Ê†ºÂºèÂåñÊñáÁ´†
  formatArticle(article) {
    if (!article) return '';
    let formatted = '';
    if (article.title) {
      formatted += `# ${article.title}\n\n`;
    }
    if (article.introduction) {
      formatted += `${article.introduction}\n\n`;
    }
    if (article.story) {
      formatted += `${article.story}\n\n`;
    }
    if (article.insights && Array.isArray(article.insights)) {
      formatted += `## ‰∫∫ÁîüÊÑüÊÇü\n\n`;
      article.insights.forEach(insight => {
        formatted += `üí≠ ${insight}\n\n`;
      });
    }
    if (article.reflection) {
      formatted += `## Ê∑±Â∫¶ÊÄùËÄÉ\n\n${article.reflection}\n\n`;
    }
    if (article.conclusion) {
      formatted += `${article.conclusion}\n\n`;
    }
    if (article.message) {
      formatted += `**‚ú® ${article.message}**\n`;
    }
    return formatted;
  }

  // Ë®àÁÆóÂ≠óÊï∏
  countWords(text) {
    if (!text) return 0;
    // ÁßªÈô§ markdown Ê®ôË®òÂíåÁâπÊÆäÂ≠óÁ¨¶ÔºåÂè™Ë®àÁÆó‰∏≠ÊñáÂ≠óÁ¨¶Êï∏
    const cleanText = text.replace(/[#*`\-\n\r]/g, '').replace(/[a-zA-Z0-9\s]/g, '');
    return cleanText.length;
  }

  // Áç≤ÂèñÊ®°Êùø
  getTemplates(category) {
    return {
      category: category,
      description: this.categories[category]
    };
  }
}

