// src/services/articleService.js
import { aiService } from './aiService';

class ArticleService {
    constructor() {
        this.categories = {
            ÊàêÈï∑: 'ÂÄã‰∫∫ÊàêÈï∑ËàáËá™ÊàëÊèêÂçá',
            ÂãµÂøó: 'ÊøÄÂãµ‰∫∫ÂøÉËàáÊ≠£ËÉΩÈáè',
            ‰∫∫ÁîüÊÑüÊÇü: '‰∫∫ÁîüÂì≤ÁêÜËàáÊô∫ÊÖß',
            ÊÑõËàáÊ∫´Êöñ: 'ÊÑõÊÉÖË¶™ÊÉÖËàá‰∫∫ÈöõÈóú‰øÇ'
        };

        this.tones = {
            Ê∫´È¶®: 'warm',
            ÊøÄÂãµ: 'inspirational', 
            Ê∑±Âàª: 'profound',
            ËºïÈ¨Ü: 'casual'
        };
    }

    // ÁîüÊàêÂøÉÈùàÈõûÊπØÊñáÁ´†
    async generateInspirationalArticle({ topic, category, tone, wordCount, useAI = false }) {
        try {
            let result;

            if (useAI && aiService) {
                // ‰ΩøÁî® AI Â¢ûÂº∑ÁâàÊú¨
                result = await aiService.enhanceInspirationalArticle(topic, category, tone, wordCount);

                if (result && result.article) {
                    return {
                        ...result,
                        wordCount: this.countWords(this.formatArticle(result.article)),
                        aiEnhanced: true,
                        aiConfidence: result.confidence || 85
                    };
                }
            }

            // Âü∫Êú¨ÁâàÊú¨ÁîüÊàê
            const basicArticle = this.generateBasicArticle(topic, category, tone, wordCount);

            return {
                article: basicArticle,
                wordCount: this.countWords(this.formatArticle(basicArticle)),
                aiEnhanced: false,
                suggestions: {
                    alternativeTitles: this.generateAlternativeTitles(topic, category),
                    improvementTips: this.getWritingTips(category),
                    relatedTopics: this.getRelatedTopics(category)
                }
            };

        } catch (error) {
            console.error('ÊñáÁ´†ÁîüÊàêÂ§±Êïó:', error);
            throw new Error('ÊñáÁ´†ÁîüÊàêÂ§±ÊïóÔºåË´ãÈáçË©¶');
        }
    }

    // ÁîüÊàêÂü∫Êú¨ÊñáÁ´†
    generateBasicArticle(topic, category, tone, targetWords) {
        const templates = this.getTemplates(category);

        return {
            title: this.generateTitle(topic, category),
            introduction: this.generateIntroduction(topic, category, tone),
            story: this.generateStory(topic, category, targetWords),
            insights: this.generateInsights(topic, category),
            reflection: this.generateReflection(topic, category),
            conclusion: this.generateConclusion(topic, tone),
            message: this.generateMessage(topic, tone)
        };
    }

    // ÁîüÊàêÊñáÁ´†Ê®ôÈ°å
    generateTitle(topic, category) {
        const titleTemplates = {
            ÊàêÈï∑: [
                `ÈóúÊñº${topic}ÔºåÊàëÊÉ≥Â∞ç‰Ω†Ë™™...`,
                `${topic}ÊïôÊúÉÊàëÁöÑ‰∫∫ÁîüÈÅìÁêÜ`,
                `Âæû${topic}‰∏≠Â≠∏Âà∞ÁöÑÊàêÈï∑Êô∫ÊÖß`,
                `${topic}ÔºöÊØèÂÄã‰∫∫ÈÉΩÈúÄË¶ÅÁü•ÈÅìÁöÑ‰∫ã`
            ],
            ÂãµÂøó: [
                `${topic}ÔºöÁõ∏‰ø°Ëá™Â∑±ÁöÑÂäõÈáè`,
                `Áï∂‰Ω†Ë¶∫Âæó${topic}ÂæàÂõ∞Èõ£ÊôÇÔºåË´ãË®ò‰Ωè...`,
                `${topic}‰∏çÊòØÁµÇÈªûÔºåËÄåÊòØËµ∑Èªû`,
                `ÊìÅÊä±${topic}ÔºåÂâµÈÄ†Â±¨Êñº‰Ω†ÁöÑÂ•áËπü`
            ],
            ‰∫∫ÁîüÊÑüÊÇü: [
                `‰∫∫ÁîüÂ¶Ç${topic}ÔºåËã¶ÁîúËá™Áü•`,
                `ÈóúÊñº${topic}ÁöÑ‰∫∫ÁîüÊÄùËÄÉ`,
                `${topic}‰∏≠Èö±ËóèÁöÑ‰∫∫ÁîüÊô∫ÊÖß`,
                `ÁîüÂëΩ‰∏≠ÁöÑ${topic}ÊôÇÂàª`
            ],
            ÊÑõËàáÊ∫´Êöñ: [
                `ÊÑõÂ¶Ç${topic}ÔºåÊ∫´Êöñ‰∫∫ÂøÉ`,
                `${topic}Ë£°ÁöÑÊÑõËàáË¢´ÊÑõ`,
                `Áî®ÂøÉÊÑüÂèó${topic}ÁöÑÊ∫´Â∫¶`,
                `${topic}ÔºöÊÑõÁöÑÂè¶‰∏ÄÁ®ÆË°®ÈÅî`
            ]
        };

        const templates = titleTemplates[category] || titleTemplates.ÊàêÈï∑;
        return templates[Math.floor(Math.random() * templates.length)];
    }

    // ÁîüÊàêÈñãÂ†¥ÊÆµËêΩ
    generateIntroduction(topic, category, tone) {
        const introTemplates = {
            Ê∫´È¶®: `Ë¶™ÊÑõÁöÑÊúãÂèãÔºåÁï∂ÊàëÂÄëË´áÂà∞${topic}ÊôÇÔºåÁ∏ΩÊòØÂÖÖÊªø‰∫ÜÊ∫´ÊöñËàáÊÑüÂãï„ÄÇÂú®ÈÄôÂÄãÂø´ÁØÄÂ•èÁöÑ‰∏ñÁïåË£°ÔºåËÆìÊàëÂÄëÂÅú‰∏ãËÖ≥Ê≠•Ôºå‰∏ÄËµ∑ÊÑüÂèóÂÖßÂøÉÊúÄÁúüÂØ¶ÁöÑËÅ≤Èü≥„ÄÇ`,
            ÊøÄÂãµ: `‰Ω†ÊúâÊ≤íÊúâÊÉ≥ÈÅéÔºå${topic}ÂÖ∂ÂØ¶ÊòØÊîπËÆäÊàëÂÄë‰∫∫ÁîüÁöÑÈóúÈçµÔºüÊØèÂÄã‰∫∫ÂøÉ‰∏≠ÈÉΩÊúâ‰∏ÄËÇ°ÂäõÈáèÔºåÁ≠âÂæÖË¢´ÂñöÈÜí„ÄÇ‰ªäÂ§©ÔºåÂ∞±ËÆìÊàëÂÄë‰∏ÄËµ∑Êé¢Á¥¢ÈÄôÂÄãÂÖÖÊªøÂèØËÉΩÊÄßÁöÑË©±È°å„ÄÇ`,
            Ê∑±Âàª: `Âú®‰∫∫ÁîüÁöÑÈï∑Ê≤≥‰∏≠Ôºå${topic}Â¶ÇÂêå‰∏ÄÈ°ÜÁü≥Â≠êÔºåÊøÄËµ∑Â±§Â±§Êº£Êº™„ÄÇÂÆÉ‰∏çÂè™ÊòØ‰∏ÄÂÄãÊ¶ÇÂøµÔºåÊõ¥ÊòØÊàëÂÄëÁêÜËß£Ëá™Â∑±„ÄÅÁêÜËß£‰∏ñÁïåÁöÑÈáçË¶ÅË¶ñËßí„ÄÇ`,
            ËºïÈ¨Ü: `ÂòøÔºåÊúãÂèãÔºÅËÆìÊàëÂÄëËºïÈ¨ÜÂú∞ËÅäËÅä${topic}ÈÄôÂÄãË©±È°å„ÄÇÁîüÊ¥ªÂ∑≤Á∂ìÂ§†Ë§áÈõú‰∫ÜÔºå‰∏çÂ¶ÇÁî®ÊúÄÁ∞°ÂñÆÁöÑÊñπÂºè‰æÜÁêÜËß£ÈÄô‰∫õÁæéÂ•ΩÁöÑ‰∫ãÁâ©„ÄÇ`
        };

        return introTemplates[tone] || introTemplates.Ê∫´È¶®;
    }

    // ÁîüÊàêÊïÖ‰∫ãÂÖßÂÆπ
    generateStory(topic, category, targetWords) {
        const storyElements = this.getStoryElements(category);
        const mainStory = storyElements.stories[Math.floor(Math.random() * storyElements.stories.length)];

        // Âü∫Á§éÊïÖ‰∫ãÂÖßÂÆπ
        let story = mainStory.replace('{‰∏ªÈ°å}', topic);

        // Êì¥Â±ïÊïÖ‰∫ãÂà∞ÁõÆÊ®ôÂ≠óÊï∏
        const wordsPerParagraph = Math.ceil(targetWords * 0.7 / 3); // ÊïÖ‰∫ã‰Ωî70%ÔºåÂàÜ3ÊÆµ

        const expandedParagraphs = [];

        // Á¨¨‰∏ÄÊÆµÔºöËÉåÊôØË®≠ÂÆö
        expandedParagraphs.push(`${story} ÈÄôÂÄãÊïÖ‰∫ãËÆìÊàëÊÉ≥Ëµ∑‰∫ÜÁîüÊ¥ª‰∏≠ÁöÑË®±Â§öÊôÇÂàªÔºåÈÇ£‰∫õÁúã‰ººÂπ≥Âá°ÂçªËòäÂê´Ê∑±ÊÑèÁöÑÁû¨Èñì„ÄÇÂú®ÊàëÂÄëÊØèÂÄã‰∫∫ÁöÑÁîüÂëΩ‰∏≠ÔºåÈÉΩÊúÉÈÅáÂà∞Ëàá${topic}Áõ∏ÈóúÁöÑÊåëÊà∞ÂíåÊ©üÈÅá„ÄÇÊúâÊôÇÂÄôÊàëÂÄëÊúÉËø∑Ëå´ÔºåÊúâÊôÇÂÄôÊàëÂÄëÊúÉÂ†ÖÂÆöÔºå‰ΩÜÁÑ°Ë´ñÂ¶Ç‰ΩïÔºåÈÄô‰∫õÁ∂ìÊ≠∑ÈÉΩÂú®Â°ëÈÄ†ËëóÊàëÂÄëÊàêÁÇ∫Êõ¥Â•ΩÁöÑËá™Â∑±„ÄÇ`);

        // Á¨¨‰∫åÊÆµÔºöÊ∑±ÂÖ•Êé¢Ë®é
        expandedParagraphs.push(`Áï∂ÊàëÂÄë‰ªîÁ¥∞ÊÄùËÄÉ${topic}ÁöÑÊú¨Ë≥™ÊôÇÔºåÊúÉÁôºÁèæÂÆÉ‰∏çÂÉÖÂÉÖÊòØË°®Èù¢‰∏äÁúãÂà∞ÁöÑÈÇ£Ê®£„ÄÇÂÆÉ‰ª£Ë°®Ëëó‰∏ÄÁ®ÆÊÖãÂ∫¶Ôºå‰∏ÄÁ®ÆÈÅ∏ÊìáÔºå‰∏ÄÁ®ÆÈù¢Â∞çÁîüÊ¥ªÁöÑÊñπÂºè„ÄÇÊØèÂÄã‰∫∫Â∞çÊñº${topic}ÁöÑÁêÜËß£ÈÉΩ‰∏çÁõ°Áõ∏ÂêåÔºåÈÄôÊ≠£ÊòØ‰∫∫ÁîüÂ§öÂΩ©ÁöÑÂú∞Êñπ„ÄÇÊúâ‰∫∫Âæû‰∏≠ÁúãÂà∞Â∏åÊúõÔºåÊúâ‰∫∫Âæû‰∏≠Â≠∏Âà∞Â†ÖÈüåÔºåÊúâ‰∫∫Âæû‰∏≠ÊÑüÂèóÂà∞ÊÑõËàáÊ∫´Êöñ„ÄÇ`);

        // Á¨¨‰∏âÊÆµÔºöÂÄã‰∫∫ÊÑüÊÇü
        expandedParagraphs.push(`ÊàëÂÄëÈÉΩÂú®Â∞ãÊâæÂ±¨ÊñºËá™Â∑±ÁöÑ${topic}Á≠îÊ°à„ÄÇÈÄôÂÄãÈÅéÁ®ãÂèØËÉΩÂÖÖÊªøÊåëÊà∞Ôºå‰ΩÜ‰πüÂÖÖÊªøÊî∂Á©´„ÄÇÈáçË¶ÅÁöÑ‰∏çÊòØÊàëÂÄëËµ∞ÂæóÂ§öÂø´ÔºåËÄåÊòØÊàëÂÄëÊòØÂê¶Âú®ÈÄôÂÄãÈÅéÁ®ã‰∏≠ÊàêÈï∑ÔºåÊòØÂê¶ËÆäÂæóÊõ¥Âä†ÁêÜËß£Ëá™Â∑±Âíå‰ªñ‰∫∫„ÄÇÊØè‰∏ÄÊ¨°ÁöÑÊÄùËÄÉÔºåÊØè‰∏ÄÊ¨°ÁöÑÂòóË©¶ÔºåÈÉΩÂú®ËÆìÊàëÂÄëÊõ¥Êé•ËøëÂÖßÂøÉÁöÑÁúüÂØ¶ËÅ≤Èü≥„ÄÇ`);

        return expandedParagraphs.join('\n\n');
